import React from "react";
import Info from "./components/info";

import WetheAutt from "./components/wethe _autt";
const API_key = "0ba9122c7db41155430fb755e75d71e0";





class App extends React.Component {
  state = {
    api: undefined,
    ex: undefined,
    temp: undefined,
    city: undefined,
    pressure: undefined,
    error: undefined

  }

  componentDidMount() {
    this.timerID = setInterval(
      () => this.gettingWether(),
      1000
    );
  }

  componentWillUnmount() {
    clearInterval(this.timerID);
  }

  getPosition=()=>{
    navigator.geolocation.getCurrentPosition((pos)=>{
    const lat = pos.coords.latitude
    const lon = pos.coords.longitude
    var London_API = `https://api.openweathermap.org/data/2.5/onecall?lat=${lat}&lon=${lon}&appid=${API_key}&lang=ru&units=metric`;
    return London_API


    })

  }





  gettingWether = async () => {

    var London_API =this.getPosition()
    const api_url = await
      fetch(London_API)
    const date = await api_url.json()
    console.log(date)


    this.setState({
      api: date,
      temp: date.current.temp,
      city: date.timezone,
      pressure: date.current.pressure,
      error: ""


    })
  }





  render() {
    return (
      <div className="conteiner">


        <Info />

        <WetheAutt
          temp={this.state.temp}
          city={this.state.city}
          pressure={this.state.pressure}
          error={this.state.error}
          api={this.state.api}
        />

      </div>



    );
  }
}

export default App
